package for_;

public class AlphabetPrint {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// for 반복문 --> A(65) ~ Z(90)
		for ( int i=65; i <= 90; i++ ) {
			System.out.print( (char)i + " " );  // A B C D E F .............................. Z 
		}
		System.out.println();
	}

}
